import { calendarSystem } from './calendarSystem';
import { firstWeekday } from './firstWeekday';
import { minimumDaysInFirstWeek } from './minimumDaysInFirstWeek';
import { localeIdentifier } from './localeIdentifier';
import { timeZone } from './timeZone';
export async function userCalendar() {
    const [identifier, firstWeekdayValue, minimumDays, locale, tz] = await Promise.all([
        calendarSystem(),
        firstWeekday(),
        minimumDaysInFirstWeek(),
        localeIdentifier(),
        timeZone(),
    ]);
    return {
        identifier,
        firstWeekday: firstWeekdayValue,
        minimumDaysInFirstWeek: minimumDays,
        localeIdentifier: locale,
        timeZone: tz,
    };
}
